import React, { useState } from "react";
import Modal from "react-modal/lib/components/Modal";
import { BrowserRouter, Route, Routes } from "react-router-dom";

import HomePage from "./Components/HomePage/HomePage";
// import { Button, Card, Container, Form, Navbar } from "react-bootstrap";
// import Logo from "./assets/img/Brasao_ufra.png";

import "./App.css";

Modal.setAppElement('#root')

const App = () => {
  // value from user
  const [inputValue, setInputValue] = useState("");



  //catch the value from the form that contain the input user
  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData);

    console.log(data);
    console.log(`chave: ${Object.keys(data)}, valor: ${Object.values(data)}`);
    setInputValue("");
  };

  //function for listen the input user
  const handleInputChange = (e) => {
    console.log(e.target.value);
    setInputValue(e.target.value);
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <HomePage
              inputValue={inputValue}
              handleSubmit={handleSubmit}
              handleInputChange={handleInputChange}
              setInputValue={setInputValue}
            />
          }
        />
        <Route
          path="/login"
          element={
            <>
              <h1>Login Page</h1>
            </>
          }
        />
      </Routes>

    </BrowserRouter>
  );
};

export default App;
