import React, { useState } from "react";
import Modal from "react-modal/lib/components/Modal";
import { BrowserRouter, Route, Routes } from "react-router-dom";

import HomePage from "./Components/HomePage/HomePage";
// import { Button, Card, Container, Form, Navbar } from "react-bootstrap";
// import Logo from "./assets/img/Brasao_ufra.png";

import "./App.css";

Modal.setAppElement("#root");

const App = () => {
  // value from user
  const [inputValue, setInputValue] = useState("");

  //catch the value from the form that contain the input user
  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData);

    console.log(data);
    console.log(`chave: ${Object.keys(data)}, valor: ${Object.values(data)}`);
    setInputValue("");
  };

  //function for listen the input user
  const handleInputChange = (e) => {
    console.log(e.target.value);
    setInputValue(e.target.value);
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <HomePage
              inputValue={inputValue}
              handleSubmit={handleSubmit}
              handleInputChange={handleInputChange}
              setInputValue={setInputValue}
            />
          }
        />
        <Route
          path="/login"
          element={
            <div class="card text-white bg-dark mb-3" >
              <form className="w-50 p-3">
                <div className="mb-3">
                  <label for="exampleInputEmail1" class="form-label">
                    Usuário
                  </label>
                  <input
                    type="email"
                    class="form-control"
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                  />
                  <div id="emailHelp" className="form-text">
                    Insira seu usuário para acessar a página do admin
                  </div>
                </div>
                <div className="mb-3">
                  <label for="exampleInputPassword1" className="form-label">
                    Senha
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="exampleInputPassword1"
                  />
                </div>
                <div className="mb-3 form-check">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id="exampleCheck1"
                  />
                  <label className="form-check-label" for="exampleCheck1">
                    Check me out
                  </label>
                </div>
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </form>
            </div>
          }
        />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
